select
  id, request_id, project_sheet_id, title, bid_sheet_num, sheet_type, tender_type, tender_mode, bid_end_time, clarify_end_time, open_bid_time, currency, tender_matters, explanation, mark, approval_process, qualification_tender, technical_tender, ranking_rules, created_user, created_time, updated_user, updated_time, description, version, deleted
  from scm_bid_sheet

    request_id is not null

        and id = #{id}


        and request_id = #{requestId}


        and project_sheet_id = #{projectSheetId}


        and title = #{title}


        and bid_sheet_num = #{bidSheetNum}


        and sheet_type = #{sheetType}


        and tender_type = #{tenderType}


        and tender_mode = #{tenderMode}


        and bid_end_time = #{bidEndTime}


        and clarify_end_time = #{clarifyEndTime}


        and open_bid_time = #{openBidTime}


        and currency = #{currency}


        and tender_matters = #{tenderMatters}


        and explanation = #{explanation}


        and mark = #{mark}


        and approval_process = #{approvalProcess}


        and qualification_tender = #{qualificationTender}


        and technical_tender = #{technicalTender}


        and ranking_rules = #{rankingRules}


        and created_user = #{createdUser}


        and created_time = #{createdTime}


        and updated_user = #{updatedUser}


        and updated_time = #{updatedTime}


        and description = #{description}


        and version = #{version}


        and deleted = #{deleted}



            AND (request_id in

                #{item}

            or title like CONCAT('%', #{keyword}, '%')
                or company_code like CONCAT('%', #{keyword}, '%')
                or purchasing_organization like CONCAT('%', #{keyword}, '%')
                or sheet_type like CONCAT('%', #{keyword}, '%')
                or tender_type like CONCAT('%', #{keyword}, '%')
                or tender_mode like CONCAT('%', #{keyword}, '%')
            )


            AND (request_id in

                #{item}

            )


            AND (
                title like CONCAT('%', #{keyword}, '%')
                or company_code like CONCAT('%', #{keyword}, '%')
                or purchasing_organization like CONCAT('%', #{keyword}, '%')
                or sheet_type like CONCAT('%', #{keyword}, '%')
                or tender_type like CONCAT('%', #{keyword}, '%')
                or tender_mode like CONCAT('%', #{keyword}, '%')
            )

